mapper.update:
UPDATE SG_MEMBER
SET MEMBER_PASSWORD = #{MEMBER_PASSWORD}, MEMBER_PHONE = #{MEMBER_PHONE}, MEMBER_ZIP = #{MEMBER_ZIP}, MEMBER_ADDR1 = #{MEMBER_ADDR1}, MEMBER_ADDR2 = #{MEMBER_ADDR2}, MEMBER_EMAIL = #{MEMBER_EMAIL}, MEMBER_HEIGHT = #{MEMBER_HEIGHT}, MEMBER_WEIGHT = #{MEMBER_WEIGHT}
WHERE MEMBER_ID = #{MEMBER_ID}

mapper.select:

SELECT COUNT(*) AS ORDER_COUNT
FROM SG_ORDER
WHERE ORDER_DELI_NO = #{ORDER_DELI_NO}

mapper.update:


     UPDATE SG_ORDER

     SET ORDER_GOODS_AMOUNT = #{ORDER_GOODS_AMOUT}

	 WHERE ORDER_NO = #{ORDER_NO}

UPDATE SG_DELI
SET DELI_RECEIVE_ZIP=#{DELI_RECEIVE_ZIP}, DELI_RECEIVE_ADDR1=#{DELI_RECEIVE_ADDR1}, DELI_RECEIVE_ADDR2=#{DELI_RECEIVE_ADDR2}, DELI_RECEIVE_NAME=#{DELI_RECEIVE_NAME}, DELI_RECEIVE_PHONE=#{DELI_RECEIVE_PHONE}, DELI_ORDER_MEMO=#{DELI_ORDER_MEMO}
WHERE DELI_NO=#{ORDER_DELI_NO}

mapper.select:

SELECT TRADE_NO
FROM SG_TRADE 
WHERE TRADE_MEMBER_ID = #{MEMBER_ID}
AND TRADE_DELI_NO = #{DELI_NO}

mapper.delete:

DELETE FROM SG_ORDER
WHERE ORDER_NO = #{ORDER_NO} 
 AND ORDER_STATE = #{ORDER_STATE}


DELETE FROM SG_ORDER
WHERE ORDER_DELI_NO = #{ORDER_DELI_NO}
AND ORDER_MEMBER_ID = #{MEMBER_ID}

mapper.update:
UPDATE SG_GOODS 
SET 
    GOODS_AMOUNT = GOODS_AMOUNT + #{ORDER_GOODS_AMOUNT},
    GOODS_SELLCOUNT = GOODS_SELLCOUNT - #{ORDER_GOODS_AMOUNT}
    WHERE GOODS_NO = #{GOODS_NO}

