)
VALUES (SG_QNA_NO_SEQ.NEXTVAL, #{QNA_CATEGORY}, #{MEMBER_NO}, #{QNA_TITLE}, #{QNA_CONTENT}, DEFAULT, SYSDATE, DEFAULT)

